import 'package:flutter/material.dart';import 'package:flutter_gen/gen_l10n/app_localizations.dart';extension CustomContext on BuildContext {  AppLocalizations get getString => AppLocalizations.of(this)!;  ThemeData getThemeData() => Theme.of(this);  MediaQueryData getMediaQuery() => MediaQuery.of(this);  void showSnackBarMessage(Text text) {    ScaffoldMessenger.of(this).showSnackBar(SnackBar(      content: text,    ));  }}